import { faBed, faCar, faPlane, faTaxi } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";

const Container = styled.div`
    background-color: #003588;
    color: white;
    display: flex;
    justify-content: center;
`;
const HeaderContainer = styled.div`
    width: 100%;
    max-width: 1024px;
    margin: 20px 0px 100px 0px;
`;
const HeaderList = styled.div`
    display: flex;
    gap: 40px;
`;

const Active = styled.div` 
    border: 1px solid white;
    padding: 10px;
    `;

const HeaderListItem = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
    &:hover ${Active}{
        border: 1px solid white;
    }
`;
const Typography = styled.span``;

const Header = () => {
  return (
    <Container>
    <HeaderContainer>
      <HeaderList>
        <HeaderListItem>
        <FontAwesomeIcon icon={faBed} />
        <Typography>Stays</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faPlane} />
        <Typography>Flights</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faCar} />
        <Typography>Car Rentals</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faBed} />
        <Typography>Attractions</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faTaxi} />
        <Typography>Airport Taxis</Typography>
        </HeaderListItem>
      </HeaderList>
      </HeaderContainer>
    </Container>
  )
}

export default Header
