import { faCalendarDays } from "@fortawesome/free-regular-svg-icons";
import { faBed, faCar, faPerson, faPlane, faTaxi } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
import { DateRange } from 'react-date-range';
import { useState } from "react";
import 'react-date-range/dist/styles.css'; // main css file
import 'react-date-range/dist/theme/default.css'; // theme css file

const Container = styled.div`
    background-color: #003588;
    color: white;
    display: flex;
    justify-content: center;
    position: relative;
`;
const HeaderContainer = styled.div`
    width: 100%;
    max-width: 1024px;
    margin: 20px 0px 100px 0px;
`;
const HeaderList = styled.div`
    display: flex;
    gap: 40px;
    margin-bottom: 50px;
`;

const Active = styled.div` 
    border: none;
    padding: 0px;
    `;

const HeaderListItem = styled.div`
    border: ${props => props.type === "active" && `1px solid white`};
    padding: ${props => props.type === "active" && `10px`};
    border-radius: ${props => props.type === "active" && `20px`};
    display: flex;
    align-items: center;
    gap: 10px;


    &:hover{
        border: 1px solid white;
        padding: 10px;
        border-radius: 20px;
    }
`;
const Typography = styled.span``;
const HeaderTitle = styled.h1``;
const HeaderDesc = styled.p`
    margin: 20px 0px;
`;
const Button = styled.button`
    background-color: #0071c2;
    color: white;
    font-weight: 500;
    border: none;
    padding: 10px;
    cursor: pointer;
`;
const HeaderSearch = styled.div`
    height: 30px;
    background-color: white;
    border: 3px solid #febb02;
    display: flex;
    align-items: center;
    justify-content: space-around;
    padding: 10px 0px;
    border-radius: 5px;
    position: absolute;
    bottom: -20px;
    width: 100%;
    max-width: 1024px;
`;
const HeaderSearchItem = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
`;
const Input = styled.input`
    border: none;
    outline: none;
`;
const HeaderTypography = styled.span`
    color: lightgray;
    cursor: pointer;
`;
const HeaderDiv = styled.div`
    color: ${props => props.type === "headerIcon" && `lightgray`}
`;


const Header = () => {
    const [date, setDate] = useState([
        {
          startDate: new Date(),
          endDate: null,
          key: 'selection'
        }
      ]);
  return (
    <Container>
    <HeaderContainer>
      <HeaderList>
      
        <HeaderListItem type="active">
        <FontAwesomeIcon icon={faBed} />
        <Typography>Stays</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faPlane} />
        <Typography>Flights</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faCar} />
        <Typography>Car Rentals</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faBed} />
        <Typography>Attractions</Typography>
        </HeaderListItem>
        <HeaderListItem>
        <FontAwesomeIcon icon={faTaxi} />
        <Typography>Airport Taxis</Typography>
        </HeaderListItem>
        
      </HeaderList>
      <HeaderTitle>A lifetime of discounts? It's Genius.</HeaderTitle>
        <HeaderDesc>
            Get rewarded for your travels | Unlock instant savings of 10% or more with a free RedoxBooking account 
        </HeaderDesc>
        <Button>Sign in/Register</Button>
        <HeaderSearch>
            <HeaderSearchItem>
            <FontAwesomeIcon icon={faTaxi} style={{"color":"lightgray"}}/>
            <Input type="text" placeholder="Where are you going?"/>
            </HeaderSearchItem>
            <HeaderSearchItem type="calendar">
            <FontAwesomeIcon icon={faCalendarDays} style={{"color":"lightgray"}}/>
            <HeaderTypography>date to date</HeaderTypography>
            <DateRange
            editableDateInputs={true}
            onChange={item => setDate([item.selection])}
            moveRangeOnFirstSelection={false}
            ranges={date}
            style={{"position":"absolute", "top":"0px",}}
            />
            </HeaderSearchItem>
            <HeaderSearchItem>
            <FontAwesomeIcon icon={faPerson} style={{"color":"lightgray"}}/>
            <HeaderTypography>2 adults 2 children 1 room</HeaderTypography>
            </HeaderSearchItem>
            <HeaderSearchItem>
            <Button type="headerButton">Search</Button>
            </HeaderSearchItem>
        </HeaderSearch>
      </HeaderContainer>
    </Container>
  )
}

export default Header
